import React from "react";

import "./form.styles.scss";

import { connect } from "react-redux";

import { joinUser } from "../../redux/join/join.action";

class JoiningForm extends React.Component {
  joiningDetails = {
    name: "",
    roomName: "",
  };

  handleChange = (event) => {
    const { name, value } = event.target;
    this.joiningDetails = {
      ...this.joiningDetails,
      [name]: value,
    };
    console.log("Joining details", this.joiningDetails);
  };

  render() {
    return (
      <div className="for">

        <input className = "form-input" name="name" type="text" onChange={this.handleChange} />
        <input className = "form-input" name="roomName" type="text" onChange={this.handleChange} />
      </div>
    );
  }
}

const mapDispatchToProps = dispatch => ({
    joinUser: joiningDetails => dispatch(joinUser(joiningDetails))
})

export default connect(null, mapDispatchToProps)(JoiningForm);
