import React from "react";
import { Link } from "react-router-dom";

class Join extends React.Component {
  constructor(props) {
    super(props);
    this.joiningDetails = {
      name: "",
      roomName: "",
    };
  }

  handleChange = (event) => {
    this.joiningDetails({
      [event.target.name]: event.target.value,
    });
    console.log("Joining details", this.joiningDetails);
  };

  render() {
    return (
      <div>
        <h1>Join component</h1>
        <input
          name="name"
          type="text"
          onChange={this.handleChange}
        />
      </div>
    );
  }
}

export default Join;
