import React from "react";

import "./form.styles.scss";

import { connect } from "react-redux";

class JoiningForm extends React.Component {

    joiningDetails = {
        name: "",
        roomName: "",
    }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.joiningDetails = {
        ...this.joiningDetails,
        [name]: value
    }
    console.log("Joining details", this.joiningDetails);
  };



    render(){

        return (
            <div>
  <input
          name="name"
          type="text"
          onChange={this.handleChange}
        />
        <input
          name="roomName"
          type="text"
          onChange={this.handleChange}
        />
            </div>

        )
    }
}
