import React from "react";

import "./form.styles.scss";

import { connect } from "react-redux";

import { joinUser } from "../../redux/join/join.action";

import { Link } from "react-router-dom";


class JoiningForm extends React.Component {
  joiningDetails = {
    name: "",
    roomName: "",
  };

  handleSubmit = event => {
      event.preventDefault();
      const {name, roomName} = this.joiningDetails;
      if (name && roomName)
      this.props.joinUser(this.joiningDetails)

  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.joiningDetails = {
      ...this.joiningDetails,
      [name]: value,
    };
    console.log("Joining details", this.joiningDetails);
  };

  render() {
    return (
      <div className="form-div">
        <form className="form" onSubmit={this.handleSubmit}>
          <input
            className="form-input"
            name="name"
            type="text"
            onChange={this.handleChange}
          />
          <input
            className="form-input"
            name="roomName"
            type="text"
            onChange={this.handleChange}
          />
          <Link to="/chat"></Link>

          <button type="submit" value="submit"/>

        </form>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  joinUser: (joiningDetails) => dispatch(joinUser(joiningDetails)),
});

export default connect(null, mapDispatchToProps)(JoiningForm);
