import React from "react";
import { connect } from "react-redux";
import JoiningForm from "../../components/form/form.component"
import { Link } from "react-router-dom";

class Join extends React.Component {

    joiningDetails = {
        name: "",
        roomName: "",
    }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.joiningDetails = {
        ...this.joiningDetails,
        [name]: value
    }
    console.log("Joining details", this.joiningDetails);
  };

  render() {
    return (
      <div>
        <h1>Join component</h1>

      </div>
    );
  }
}

export default Join;
